import './App.css';
import { useEffect, useState } from 'react'
import { Octokit } from 'octokit'
import RepoCard from './components/RepoCard';
import Loading from './components/Loading';

function App() {
  const octokit = new Octokit({});
  const [query, setQuery] = useState('');
  const [sortText, setSortText] = useState('');
  const [repoList, setRepoList] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchData = async () => {
    setLoading(true);
    try {
      if (query) {
        let resList = await octokit.request('GET /search/repositories', {
          q: query,
          sort: sortText,
        })
        setRepoList(resList.data.items);
      }
      setLoading(false);
    }
    catch (err) {
      setLoading(false);
      console.error(err)
    }
  }

  const debounceQuery = (apiCallback, delay) => {
    console.log('k')
    let timer;
    console.log(timer)
    return function() {
      if(timer) clearTimeout(timer);
      timer = setTimeout(() => {
        apiCallback()
        console.log('l')
      }, delay)
    }
  }
  const debounceQueryMethod = debounceQuery(fetchData, 4000)
  useEffect(() => {
    debounceQueryMethod();
  }, [query])

  return (
    <div className="App">
      <input
        align="middle"
        className='search'
        placeholder='Search...'
        onChange={e => setQuery(e.target.value)}
      />
      <div >
        <div style={{display: 'inline-block'}} className="text-md">Sort By</div>
        <select name="sort" id="cars">
          <option value="volvo">Volvo</option>
          <option value="saab">Saab</option>
          <option value="mercedes">Mercedes</option>
          <option value="audi">Audi</option>
        </select>
      </div>

      {loading ?
        <Loading /> :
        (<section className='cards'>
          {repoList && repoList.length > 0 && repoList.map((item) => (
            <RepoCard key={item.id} repo={item} />
          ))}
        </section>)}

    </div>
  );
}

export default App;
