import './App.css';
import { useEffect, useState } from 'react'
import { Octokit } from 'octokit'
import RepoCard from './components/RepoCard';
import Loading from './components/Loading';

function App() {
  const octokit = new Octokit({});
  const [query, setQuery] = useState('');
  const [sortText, setSortText] = useState('');
  const [repoList, setRepoList] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchData = () => {
    return new Promise(async (resolve,reject) => {
      let res = await octokit.request('GET /search/repositories', {
        q: query,
        sort: sortText,
      })
      if(res) {
        setRepoList(repoList);
        resolve(true);
      } 
      else reject(false);
    })
  }

  useEffect(() => {
    setLoading(true);
    let timer;
    try {
      if (query) {
        timer = setTimeout(async () => {
          await fetchData();
        }, 2000)
      }
      setLoading(false);
    }
    catch (err) {
      setLoading(false);
      console.error(err)
    }
    return () => clearTimeout(timer)
  }, [query])

  useEffect(() => {
    fetchData();
  },[sortText])

  return (
    <div className="App">
      <input
        align="middle"
        className='search'
        placeholder='Search...'
        value={query}
        onChange={e => setQuery(e.target.value)}
      />
      <div >
        <div style={{ display: 'inline-block' }} className="text-md">Sort By</div>
        <select name="sortText" id="cars">
          <option value="volvo">Volvo</option>
          <option value="saab">Saab</option>
          <option value="mercedes">Mercedes</option>
          <option value="audi">Audi</option>
        </select>
      </div>

      {loading ?
        <Loading /> :
        (<section className='cards'>
          {repoList && repoList.length > 0 && repoList.map((item) => (
            <RepoCard key={item.id} repo={item} />
          ))}
        </section>)}

    </div>
  );
}

export default App;
